var searchQuery =  function(data){
  query = "";
  
  //select
  length = data.select.length;
  query += "SELECT ";
  for(i=0;i<length;i++){
    query += data.select[i]
    if(i<length-1)
      query += ", ";
    else query += " ";
  }
  //from
  query += " FROM "+ data.from;
  //where
  query += " WHERE ";
  length = data.where.length;
  for(i=0;i<length;i++){
    query += data.where[i][0]+data.where[i][1]+"'"+data.where[i][2]+"'";
    if(i<length-1)
      query +=" AND ";
  
  }
  return query;
}